import React from "react";
import Sidenav from "./Sidenav";
import Button from "@material-ui/core/Button";
import "./Person.css";
const url = "http://localhost:8000/api/persons";

const buttonStyle= {
    backgroundColor: "#20df7f",
    border: "none",
    color: "white",
    fontFamily: "Lexend Deca, sans-serif",
    textAlign: "center",
    textDecoration: "none",
    fontSize: "16px",
    fontWeight: 600,
    marginLeft: "10px",
    marginRight: "10px",
    marginTop: "10px",
    cursor: "pointer"
}

export default function PersonPage() {
    var page = 0;
    var no_per_page = 10;
    var persons = [];
    function showAll() {
        fetch(url + "/get/" + page + "/" + no_per_page,{
            method: "GET",
            headers: {
                Accept: "application/json",
                Authorization: "Bearer " + localStorage.getItem("token"),
            }
        })
        .then((res) => res.json())
        .then((data) => {
            persons = data;
        });
    }
    return (
        <div className="PersonsPage">
            <Sidenav/>
            <main>
                <Button variant="contained" style={buttonStyle} >Add new person</Button>
                <Button variant="contained" style={buttonStyle} onClick={showAll}>Show all</Button>
                <div>
                    {persons.map(person => (
                        <Item key={person.id} item={person.first_name} />
                    ))}
                </div>
            </main>
        </div>
    );
}
