import React from "react";
import { useState, useEffect } from "react";
import Sidenav from "./Sidenav";
import Button from "@material-ui/core/Button";
import "./Person.css";
import { Delete, Edit } from "@mui/icons-material";
const url = "http://localhost:8000/api/person";

const buttonStyle= {
    backgroundColor: "#20df7f",
    border: "none",
    color: "white",
    fontFamily: "Lexend Deca, sans-serif",
    textAlign: "center",
    textDecoration: "none",
    fontSize: "16px",
    fontWeight: 600,
    marginLeft: "10px",
    marginRight: "10px",
    marginTop: "10px",
    cursor: "pointer",
    boxShadow: "0 4px 8px 0 rgba(0,0,0,0.2), 0 6px 20px 0 rgba(0,0,0,0.19)",
}

export default function PersonPage() {
    const [persons, setPersons] = useState([]);
    var page = 0;
    var no_per_page = 10;

    const fetchPersons = () => {
        fetch(url + "/get/" + page + "/" + no_per_page,{
            method: "GET",
            headers: {
                Accept: "application/json",
                Authorization: "Bearer " + localStorage.getItem("token"),
            }
        })
        .then((res) => res.json())
        .then((data) => {
            setPersons(data);
        });
    }
    useEffect(() => {
        fetchPersons()
    }, [])

    return (
        <div className="PersonsPage">
            <Sidenav/>
            <main>
                <Button variant="contained" style={buttonStyle} >Add new person</Button>
                {/* <Button variant="contained" style={buttonStyle} onClick={fetchPersons}>Show all</Button> */}

                <h1>All person</h1>
                <div className="container">
                    {
                        persons.map((person) => (
                            <div key={person.person_id} className="person">
                                <div className="personData">
                                    <p>{person.first_name + " " + person.last_name}</p>
                                    <p>Area: {person.area} ha</p>
                                    <p>Remained quantity: {(parseFloat(person.area) * parseFloat(person.quantity)).toFixed(2)} kg</p>
                                </div>
                                <div className="personButtons">
                                    <div className="dataButtons">
                                        <Button startIcon={<Edit/>} variant="contained" style={buttonStyle}>Edit</Button>
                                        <Button startIcon={<Delete/>} variant="contained" style={buttonStyle}>Delete</Button>
                                        <Button variant="contained" style={buttonStyle}>Show </Button>
                                    </div>
                                    <div className="receiptButtons">
                                        <Button variant="contained" style={buttonStyle}>New receipt</Button>
                                        <Button variant="contained" style={buttonStyle}>All receipts</Button>
                                    </div>
                                </div>
                            </div>
                        ))
                    }
                </div>
            </main>
        </div>
    );
}
